Array.prototype.forEach.call(
  document.querySelectorAll(".file-upload__button"),
  function (button) {
    const hiddenInput = button.parentElement.querySelector(
      ".file-upload__input"
    );
    const label = button.parentElement.querySelector(".file-upload__label");
    const defaultLabelText = "No file(s) selected";

    // Set default text for label
    label.textContent = defaultLabelText;
    label.title = defaultLabelText;

    button.addEventListener("click", function () {
      hiddenInput.click();
    });

    hiddenInput.addEventListener("change", function () {
      const filenameList = Array.prototype.map.call(
        hiddenInput.files,
        function (file) {
          return file.name;
        }
      );

      label.textContent = filenameList.join(", ") || defaultLabelText;
      label.title = label.textContent;
    });
  }
);

$.getJSON(
  "https://raw.githubusercontent.com/umut79/dinamik-ililce-json/master/tr-il.json",
  function (sonuc) {
    $("#city").html("<option>Seçiniz</option>");
    $("#district").html("<option>Seçiniz</option>");
    $.each(sonuc, function (index, value) {
      var row = "";
      row +=
        '<option value="' + value.il_id + '">' + value.il_adi + "</option>";
      $("#city").append(row);
    });
  }
);


$("#city").on("change", function () {
  var il = $(this).val();
  $("#district").html("");
  $.getJSON(
    "https://raw.githubusercontent.com/umut79/dinamik-ililce-json/master/tr-ilce.json",
    function (sonuc) {
      $("#district").html("<option>Seçiniz</option>");
      $.each(sonuc, function (index, value) {
        var row = "";
        if (value.il_id == il) {
          row +=
            '<option value="' +
            value.ilce_id +
            '">' +
            value.ilce_adi +
            "</option>";
          $("#district").append(row);
        }
      });
    }
  );
});

